-maxlen=2500 
-case=${letter} 
-seqnos=${seqnos} 
-outorder=${outorder} 
-run_name=result
-multi_core=${settings.multicore}
-quiet=stdout; t_coffee -other_pg trmsd result.aln -template_file result_pdb1.template_list -output color_html 2>&1; [ -e result.struc_tree.consensus ]
